import React, { useState } from 'react'
import "./score.css"

const Score = () => {
    const [runShow, setRunShow] = useState([]);
    const [ball, setBall] = useState(1)
    const [currentRun, setCurrentRun] = useState(0)
    const [wicket, setWicket] = useState(0)
    const [group, setGroup] = useState("groupA")
    const [totalrun, setTotalRun] = useState(0)
    const [over, setOver] = useState(0)

    const handleScore = (run) => {

        if (wicket <= 10) {

            if (ball <= 6) {
                setRunShow([...runShow, run])
                setCurrentRun(currentRun + parseInt(run))
                setOver(round(over+0.1))
                // console.log(ball)
                if (run !== "Wd" && run !== "Nb") {
                    setBall(ball + 1)
                }
            }
        }

    }

    const newOver = () => {
        setBall(1)
        setRunShow([])
        setOver(over + 1)
        setCurrentRun(currentRun)
    }
    return (
        <div className="container">

            {
                group === "groupA" ?


                    wicket === 10 || over >= 5 ?
                        <div className="centered-content">
                            <div className="score">
                                <h2>Match Score</h2>
                                <h4>Current Score : {currentRun} Runs and {wicket} Wickets</h4>

                                <button onClick={() => {
                                    setTotalRun(currentRun)
                                    setWicket(0)
                                    setCurrentRun(0)
                                    setBall(1)
                                    setRunShow([])
                                    setOver(0)
                                    setGroup("GroupB")
                                }}>Second Innings</button>
                            </div>
                        </div>
                        :

                        <div className="centered-content">
                            <div className="runShow">
                                {
                                    runShow.map((run, index) => (
                                        <p key={index}>{run}</p>
                                    ))
                                }
                            </div>
                            <div className="run">
                                <button onClick={() => handleScore("1")}>1</button>
                                <button onClick={() => handleScore("2")}>2</button>
                                <button onClick={() => handleScore("3")}>3</button>
                                <button onClick={() => handleScore("4")}>4</button>
                                <button onClick={() => handleScore("5")}>5</button>
                                <button onClick={() => handleScore("6")}>6</button>
                            </div>
                            <div className="guid">
                                <button onClick={() => {
                                    handleScore("Wd")
                                    setCurrentRun(currentRun + 1)
                                }}>Wide Ball</button>
                                <button onClick={() => {
                                    handleScore("Nb")
                                    setCurrentRun(currentRun + 1)
                                }}>No Ball</button>
                                <button onClick={() => {
                                    handleScore("W")
                                    setWicket(wicket + 1)
                                    setCurrentRun(currentRun)
                                }}>Wicket</button>
                                <button onClick={() => handleScore("0")}>Dot Ball</button>
                                {ball <= 6 ? "" : <button onClick={newOver}>New Over</button>}
                            </div>

                            <div className="score">
                                <h2>Match Score</h2>
                                <h4>Current Score : {currentRun} Runs and {wicket} Wickets</h4>
                                <h4>over - {over} / 5</h4>
                            </div>
                        </div>

                    :
                    totalrun < currentRun ?
                        totalrun === currentRun ? "Match tie!!" :
                            <div className="centered-content">{totalrun < currentRun ? <h2>GroupB - Match Winn!! </h2> : <h2> GroupA - Match Winn!! </h2>}</div> :
                        <div className="centered-content">
                            <div className="runShow">
                                {
                                    runShow.map((run, index) => (
                                        <p key={index}>{run}</p>
                                    ))
                                }
                            </div>
                            <div className="run">
                                <button onClick={() => handleScore("1")}>1</button>
                                <button onClick={() => handleScore("2")}>2</button>
                                <button onClick={() => handleScore("3")}>3</button>
                                <button onClick={() => handleScore("4")}>4</button>
                                <button onClick={() => handleScore("5")}>5</button>
                                <button onClick={() => handleScore("6")}>6</button>
                            </div>
                            <div className="guid">
                                <button onClick={() => {
                                    handleScore("Wd")
                                    setCurrentRun(currentRun + 1)
                                }}>Wide Ball</button>
                                <button onClick={() => {
                                    handleScore("Nb")
                                    setCurrentRun(currentRun + 1)
                                }}>No Ball</button>
                                <button onClick={() => {
                                    handleScore("W")
                                    setWicket(wicket + 1)
                                    setCurrentRun(currentRun)
                                }}>Wicket</button>
                                <button onClick={() => handleScore("0")}>Dot Ball</button>
                                {ball <= 6 ? "" : <button onClick={newOver}>New Over</button>}
                            </div>

                            <div className="score">
                                <h2>Match Score</h2>
                                <h4>Current Score : {currentRun} Runs and {wicket} Wickets</h4>
                                <h4>over - {over} / 5</h4>
                            </div>
                        </div>

            }
        </div>
    )
}

export default Score
